     1→// Quiz generator that uses handcrafted questions from topic data
     2→export const generateQuiz = (topic) => {
     3→  if (!topic) return [];
     4→  
     5→  // If the topic has handcrafted questions, use those
     6→  if (topic.questions && Array.isArray(topic.questions) && topic.questions.length > 0) {
     7→    // Shuffle the questions and return them
     8→    return topic.questions
     9→      .map(q => ({
    10→        question: q.question,
    11→        answer: q.answer,
    12→        options: q.options || []
    13→      }))
    14→      .filter(q => q.question && q.answer && q.options.length >= 2)
    15→      .sort(() => 0.5 - Math.random());
    16→  }
    17→  
    18→  // Fallback: return empty array if no questions found
    19→  return [];
    20→};
    21→

