    15→
    16→  const questions = useMemo(() => {
    17→    if (!topic) return [];
    18→    return generateQuiz(topic);
    19→  }, [topic]);
    20→
    21→  useEffect(() => {
    22→    // Entrance animation on mount (which happens on every topic change due to key prop)
    23→    gsap.fromTo(containerRef.current, 
    24→      { opacity: 0, y: 50 }, 
    25→      { opacity: 1, y: 0, duration: 0.8, ease: 'power3.out' }
    26→    );
    27→  }, []);
