   140→                  icon = <X className="w-5 h-5" />;
   141→                } else {
   142→                    containerClass = "border-black/5 opacity-40 grayscale";
   143→                }
   144→              } else if (isSelected) {
   145→                containerClass = "border-accent bg-accent/5 text-accent";
   146→              }
   147→
   148→              return (
   149→                <button
   150→                  key={idx}
   151→                  onClick={() => handleAnswer(option)}
   152→                  disabled={isAnswered}
   153→                  className={`w-full p-5 text-left rounded-xl transition-all duration-300 font-medium relative flex items-center justify-between ${containerClass}`}
   154→                >
   155→                  <span className="text-lg">{option}</span>
   156→                  {icon}
   157→                </button>
   158→              );
   159→            })}
   160→          </div>
   161→
   162→          <div className="mt-12 flex justify-end">
   163→             <button 
   164→                onClick={nextQuestion} 
   165→                disabled={!isAnswered}
   166→                className={`group flex items-center gap-3 text-lg font-bold transition-all ${
   167→                    isAnswered 
   168→                        ? 'text-ink hover:gap-5' 
   169→                        : 'text-gray-300 cursor-not-allowed'
   170→                }`}
   171→            >
   172→                {currentQ === questions.length - 1 ? 'Finish' : 'Next'}
   173→                <ArrowRight className={`w-6 h-6 ${isAnswered ? 'text-accent' : ''}`} />
   174→            </button>
   175→          </div>
   176→
   177→        </div>
   178→      </div>
   179→    </div>
   180→  );
   181→};
   182→
   183→export default QuizInterface;

