     1→import React, { useState, useEffect, useRef, useMemo } from 'react';
     2→import { generateQuiz } from '../utils/quizGenerator';
     3→import { Brain, ArrowRight, RefreshCw, X, Check } from 'lucide-react';
     4→import gsap from 'gsap';
     5→
     6→const QuizInterface = ({ topic }) => {
     7→  const [currentQ, setCurrentQ] = useState(0);
     8→  const [score, setScore] = useState(0);
     9→  const [showResult, setShowResult] = useState(false);
    10→  const [selectedOption, setSelectedOption] = useState(null);
    11→  const [isAnswered, setIsAnswered] = useState(false);
    12→  
    13→  const cardRef = useRef(null);
    14→  const containerRef = useRef(null);
    15→
    16→  const questions = useMemo(() => {
    17→    if (!topic) return [];
    18→    return generateQuiz(topic);
    19→  }, [topic]);
    20→
    21→  useEffect(() => {
    22→    // Entrance animation on mount (which happens on every topic change due to key prop)
    23→    gsap.fromTo(containerRef.current, 
    24→      { opacity: 0, y: 50 }, 
    25→      { opacity: 1, y: 0, duration: 0.8, ease: 'power3.out' }
    26→    );
    27→  }, []);
    28→
    29→  const resetQuiz = () => {
    30→    setCurrentQ(0);
    31→    setScore(0);
    32→    setShowResult(false);
    33→    setSelectedOption(null);
    34→    setIsAnswered(false);
    35→    gsap.fromTo(containerRef.current, { opacity: 0, y: 50 }, { opacity: 1, y: 0, duration: 0.8, ease: 'power3.out' });
    36→  };
    37→
    38→  const handleAnswer = (option) => {
    39→    if (isAnswered) return;
    40→    setSelectedOption(option);
    41→    setIsAnswered(true);
    42→    if (option === questions[currentQ].answer) {
    43→      setScore(s => s + 1);
    44→    }
    45→  };
    46→
    47→  const nextQuestion = () => {
    48→    // Card exit animation
    49→    gsap.to(cardRef.current, {
    50→      x: -50,
    51→      opacity: 0,
    52→      duration: 0.3,
    53→      ease: 'power2.in',
    54→      onComplete: () => {
    55→        if (currentQ < questions.length - 1) {
    56→          setCurrentQ(c => c + 1);
    57→          setSelectedOption(null);
    58→          setIsAnswered(false);
    59→          // Card enter animation
    60→          gsap.fromTo(cardRef.current, { x: 100, opacity: 0 }, { x: 0, opacity: 1, duration: 0.5, ease: 'power3.out' });
    61→        } else {
    62→          setShowResult(true);
    63→        }
    64→      }
    65→    });
    66→  };
    67→
    68→  if (!topic) return null;
    69→  if (questions.length === 0) return (
    70→     <div className="flex flex-col items-center justify-center h-[50vh] text-subtle">
    71→        <Brain className="w-12 h-12 mb-4 opacity-50" />
    72→        <p>No quiz data available.</p>
    73→     </div>
    74→  );
    75→
    76→  if (showResult) {
    77→    return (
    78→      <div className="max-w-md mx-auto mt-20 text-center">
    79→        <div className="relative bg-surface p-12 rounded-2xl border-2 border-ink shadow-hard">
    80→          <div className="w-20 h-20 bg-accent text-white rounded-full flex items-center justify-center mx-auto mb-8 text-3xl font-bold">
    81→            {Math.round((score / questions.length) * 100)}%
    82→          </div>
    83→          
    84→          <h2 className="text-4xl font-serif font-bold text-ink mb-4">Quiz Finished</h2>
    85→          <p className="text-subtle mb-10 text-lg">You scored {score} out of {questions.length}</p>
    86→
    87→          <button 
    88→            onClick={resetQuiz} 
    89→            className="w-full flex items-center justify-center gap-2 bg-ink text-white px-8 py-4 rounded-xl font-bold hover:bg-accent transition-colors duration-300"
    90→          >
    91→            <RefreshCw className="w-5 h-5" />
    92→            Again
    93→          </button>
    94→        </div>
    95→      </div>
    96→    );
    97→  }
    98→
    99→  const question = questions[currentQ];
   100→  const progress = ((currentQ + 1) / questions.length) * 100;
   101→
   102→  return (
   103→    <div ref={containerRef} className="max-w-2xl mx-auto py-12 px-6">
   104→      
   105→      {/* Header Info */}
   106→      <div className="flex justify-between items-end mb-8 text-sm font-bold uppercase tracking-widest text-subtle">
   107→        <span>Question {currentQ + 1} <span className="text-gray-300">/</span> {questions.length}</span>
   108→        <span>Score: {score}</span>
   109→      </div>
   110→
   111→      <div className="w-full bg-black/5 h-1 mb-12">
   112→        <div className="bg-accent h-full transition-all duration-700 ease-in-out" style={{ width: `${progress}%` }} />
   113→      </div>
   114→      
   115→      <div ref={cardRef} className="relative group perspective-1000">
   116→        {/* Shadow Element */}
   117→        <div className="absolute inset-0 bg-ink translate-x-3 translate-y-3 rounded-2xl transition-transform duration-300" />
   118→        
   119→        {/* Main Card */}
   120→        <div className="relative bg-surface rounded-2xl border-2 border-ink p-8 md:p-12">
   121→            
   122→          <h3 className="text-2xl md:text-3xl font-serif font-medium leading-relaxed text-ink mb-10">
   123→            {question.question}
   124→          </h3>
   125→          
   126→          <div className="space-y-4">
   127→            {question.options.map((option, idx) => {
   128→              const isSelected = selectedOption === option;
   129→              const isCorrect = option === question.answer;
   130→              
   131→              let containerClass = "border-2 border-black/10 hover:border-black/30 hover:bg-black/5";
   132→              let icon = null;
   133→
   134→              if (isAnswered) {
   135→                if (isCorrect) {
   136→                  containerClass = "border-green-500 bg-green-50 text-green-800";
   137→                  icon = <Check className="w-5 h-5" />;
   138→                } else if (isSelected) {
   139→                  containerClass = "border-red-500 bg-red-50 text-red-800";
   140→                  icon = <X className="w-5 h-5" />;
   141→                } else {
   142→                    containerClass = "border-black/5 opacity-40 grayscale";
   143→                }
   144→              } else if (isSelected) {
   145→                containerClass = "border-accent bg-accent/5 text-accent";
   146→              }
   147→
   148→              return (
   149→                <button
   150→                  key={idx}
   151→                  onClick={() => handleAnswer(option)}
   152→                  disabled={isAnswered}
   153→                  className={`w-full p-5 text-left rounded-xl transition-all duration-300 font-medium relative flex items-center justify-between ${containerClass}`}
   154→                >
   155→                  <span className="text-lg">{option}</span>
   156→                  {icon}
   157→                </button>
   158→              );
   159→            })}
   160→          </div>
   161→
   162→          <div className="mt-12 flex justify-end">
   163→             <button 
   164→                onClick={nextQuestion} 
   165→                disabled={!isAnswered}
   166→                className={`group flex items-center gap-3 text-lg font-bold transition-all ${
   167→                    isAnswered 
   168→                        ? 'text-ink hover:gap-5' 
   169→                        : 'text-gray-300 cursor-not-allowed'
   170→                }`}
   171→            >
   172→                {currentQ === questions.length - 1 ? 'Finish' : 'Next'}
   173→                <ArrowRight className={`w-6 h-6 ${isAnswered ? 'text-accent' : ''}`} />
   174→            </button>
   175→          </div>
   176→
   177→        </div>
   178→      </div>
   179→    </div>
   180→  );
   181→};
   182→
   183→export default QuizInterface;

